import React from 'react'
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { useEffect, useState } from "react";

AOS.init();

export const NavBar = () => {
  const [activeLink, setActiveLink] = useState("all");
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  };

  // OPACITY TOGGLE

  const setFocused = () => {
    localStorage.setItem("opacity", "focused");
    document.documentElement.setAttribute("data-theme", "focused");
  };
  const setUnfocused = () => {
    localStorage.setItem("opacity", "unfocused");
    document.documentElement.setAttribute("data-theme", "unfocused");
  };

  const toggleOpacity = (e) => {
    if (e.target.checked) {
      setFocused();
    } else {
      setUnfocused();
    }
  };

  return (
    <header>
      <Navbar expand="lg" className={scrolled ? "scrolled" : ""}>
        <Container>
          {/* <p className="toggle">Toggle Theme</p> */}
          <Navbar.Brand href="#home">
            <DarkMode />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav">
            <span className="navbar-toggler-icon"></span>
          </Navbar.Toggle>
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link
                href="#home"
                className={
                  activeLink === "home" ? "active navbar-Link" : "navbar-Link"
                }
                onClick={() => onUpdateActiveLink("home")}
              >
                Home
              </Nav.Link>
              <Nav.Link
                href="#about"
                className={
                  activeLink === "about" ? "active navbar-Link" : "navbar-Link"
                }
                onClick={() => onUpdateActiveLink("about")}
              >
                About
              </Nav.Link>
              <Nav.Link
                href="#projects"
                className={
                  activeLink === "projects"
                    ? "active navbar-Link"
                    : "navbar-Link"
                }
                onClick={() => onUpdateActiveLink("projects")}
              >
                Projects
              </Nav.Link>

              <Nav.Link
                href="#contact"
                className={
                  activeLink === "contact" ? "active navbar-Link" : "navbar-Link"
                }
                onClick={() => onUpdateActiveLink("contact")}
              >
                Contact
              </Nav.Link>
            </Nav>
            <span className="navbar-text">
              <div className="social-icon">
                <a
                  href="https://www.linkedin.com/in/hunter-brennan-523000241/"
                  target="_blank"
                  data-aos="fade-down"
                  data-aos-duration="1500"
                >
                  <img src={navIcon1} alt="Linkedin" />
                </a>
                <a
                  href="https://github.com/HunterBrennan1"
                  target="_blank"
                  data-aos="fade-down"
                  data-aos-duration="1800"
                >
                  <img src={navIcon2} alt="Github" />
                </a>
                <a
                  href="https://twitter.com/hunterb00733201"
                  target="_blank"
                  data-aos="fade-down"
                  data-aos-duration="2100"
                >
                  <img src={navIcon3} alt="Twitter" />
                </a>
              </div>
              <button
                href="contact"
                className="vvd"
                onClick={() => console.log("connect")}
              >
                Resume
              </button>
            </span>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};
